using FluentValidation; namespace ESGDiversity.API.Validators { public class PaginationValidator : AbstractValidator<(int page, int pageSize)> { public PaginationValidator() { RuleFor(x => x.page).GreaterThan(0).WithMessage("Page must be greater than 0"); RuleFor(x => x.pageSize).GreaterThan(0).WithMessage("PageSize must be greater than 0").LessThanOrEqualTo(100).WithMessage("PageSize must not exceed 100"); }}}
