namespace ESGDiversity.API.ViewModels { public class PaginatedResponse<T> { public int Page { get; set; } public int PageSize { get; set; } public int TotalCount { get; set; } public int TotalPages { get; set; } public IEnumerable<T> Data { get; set; } = new List<T>(); public bool HasPrevious => Page > 1; public bool HasNext => Page < TotalPages; }}
